 2752	 }
 2753	
 2754	 // Get the matrix actions compendium
 2755	 const matrixPack = game.packs.get("sr6elysium.matrix-actions");
 2756	 if (!matrixPack) {
 2757	 console.error("Shadowrun 6e | Matrix Actions compendium not found");
 2758	 return;
 2759	 }
 2760	
 2761	 // Get all matrix actions
 2762	 await matrixPack.getIndex();
 2763	 const matrixActions = await Promise.all(
 2764	 Array.from(matrixPack.index).map(i => matrixPack.getDocument(i._id))
 2765	 );
 2766	
 2767	 // Get existing action names to avoid duplicates
 2768	 const existingNames = this.items
 2769	 .filter(i => i.type === "action")
 2770	 .map(i => i.name.toLowerCase());
 2771	
 2772	 // Filter out actions the actor already has
 2773	 const actionsToAdd = matrixActions.filter(
 2774	 a => !existingNames.includes(a.name.toLowerCase())
 2775	 );
 2776	
 2777	 if (actionsToAdd.length === 0) {
 2778	 console.log(`Shadowrun 6e | ${this.name} already has all matrix actions`);
 2779	 // Set the flag to indicate that we've checked for matrix actions
 2780	 await this.setFlag('sr6elysium', 'hasMatrixActions', true);
 2781	 return;
 2782	 }
 2783	
