 2785	 await this.createEmbeddedDocuments(
 2786	 "Item",
 2787	 actionsToAdd.map(a => a.toObject())
 2788	 );
 2789	
 2790	 console.log(`Shadowrun 6e | Added ${actionsToAdd.length} matrix actions to ${this.name}`);
 2791	
 2792	 // Set the flag to indicate that we've added matrix actions
 2793	 await this.setFlag('sr6elysium', 'hasMatrixActions', true);
