 12	 * Prepare Matrix data on the actor
 13	 * - if an item is equipped, it will use that data
 14	 * - if it isn't and player is technomancer, it will use that data
 15	 */
 16	 static prepareMatrix(system: ActorTypesData & MatrixActorData, items: SR6ItemDataWrapper[]) {
 17	 const { matrix, attributes, modifiers } = system;
 18	
 19	 const MatrixList = ['firewall', 'sleaze', 'data_processing', 'attack'];
 20	
 21	 // clear matrix data to defaults
 22	 MatrixList.forEach((key) => {
 23	 const parts = new PartsList(matrix[key].mod);
 24	 if (matrix[key].temp) parts.addUniquePart('SR6.Temporary', matrix[key].temp);
 25	 // LEGACY from when the sheet used 'mod.Temporary'
 26	 parts.removePart('Temporary');
 27	 matrix[key].mod = parts.list;
 28	 matrix[key].value = parts.total;
 29	 });
