 44	 /**
 45	 * On initial actor creation the matrix track will be missing.
 46	 *
 47	 * This is intentional as not to pollute template.json with actor type specific data.
 48	 *
 49	 */
 50	 static addMissingTracks(system: ICData) {
 51	 // Newly created actors SHOULD have this by template.
 52	 // Legacy actors MIGHT not have it, therefore make sure it's there.
 53	 const track = system.track || {};
 54	 if (!track.matrix) track.matrix = DataDefaults.trackData();
 55	 system.track = track;
 56	 }
