 489	 game.socket.on(SYSTEM_SOCKET, async (message: Shadowrun.SocketMessageData) => {
 490	 console.log('Shadowrun 6e | Received system socket message.', message);
 491	
 492	 const handlers = hooks[message.type];
 493	 if (!handlers || handlers.length === 0) return console.warn('Shadowrun 6e | System socket message has no registered handler!', message);
 494	 // In case of targeted socket message only execute with target user (intended for GM usage)
 495	 if (message.userId && game.user?.id !== message.userId) return;
 496	 if (message.userId && game.user?.id) console.log('Shadowrun 6e | GM is handling system socket message');
 497	
 498	 for (const handler of handlers) {
 499	 console.debug(`Shadowrun 6e | Handover system socket message to handler: ${handler.name}`);
 500	 await handler(message);
 501	 }
 502	 });
 503	 }
