 36	/**
 37	 * The general Shadowrun actor implementation, which currently handles all actor types.
 38	 *
 39	 * To easily access ActorData without any typing issues us the SR6Actor.asCritter helpers.
 40	 * They are set up in a way that will handle both error management and type narrowing.
 41	 * Example:
 42	 * <pre><code>
 43	 * const actor = game.actors.get('randomId');
 44	 * const critter = actor.asCritter();
 45	 * if (!critter) return;
 46	 * // critter.type === 'critter'
 47	 * // critter.system as CritterData
 48	 * </code></pre>
 49	 *
 50	 */
 51	export class SR6Actor extends Actor {
 52	 /**
 53	 * Cache for armor calculations to avoid recalculating unnecessarily
 54	 * @type {Object}
 55	 * @private
 56	 */
 57	 private _armorCache: {
 58	 armor?: Shadowrun.ActorArmor,
 59	 timestamp: number,
 60	 equipmentHash: string
 61	 } = {
 62	 timestamp: 0,
...
 1450	
 1451	 /** Return either the linked token or the token of the synthetic actor.
 1452	 *
 1453	 * @return Will return null should no token have been placed on scene.
 1454	 */
 1455	 getToken(): TokenDocument | null {
 1456	 // Linked actors can only have one token, which isn't stored within actor data...
 1457	 if (this._isLinkedToToken() && this.hasToken()) {
 1458	 const linked = true;
 1459	 const tokens = this.getActiveTokens(linked) as unknown as Token[];
 1460	 // This assumes for a token to exist and should fail if not.
 1461	 return tokens[0].document;
 1462	 }
 1463	
 1464	 // Unlinked actors can have multiple active token but each have theirs directly attached...
 1465	 return this.token;
