 1	/**
 2	 * Handle all things related to the action template (template.json)
 3	 */
 4	import { SR6Actor } from "../../actor/SR6Actor";
 5	import { Helpers } from "../../helpers";
 6	import { SR6Item } from "../SR6Item";
 7	import { PartsList } from "../../parts/PartsList";
 8	import { SR6 } from "../../config";
 9	import { DataDefaults } from "../../data/DataDefaults";
 10	import { Translation } from "../../utils/strings";
 11	
 12	export class ActionFlow {
 13	 /**
 14	 * Calculate action damage configuration based on flat damage field and possible dynamic attribute modification.
 15	 *
 16	 * @param damage The damage field as defined within the ActionData
 17	 * @param actor The actor to use should a dynamic calculation be needed.
 18	 * @param item
 19	 */
 20	 static calcDamageData(damage: Shadowrun.DamageData, actor?: SR6Actor, item?: SR6Item): Shadowrun.DamageData {
 21	 // Avoid manipulation on original data, which might come from database values.
 22	 damage = foundry.utils.duplicate(damage);
 23	
 24	 if (!actor) return damage;
 25	
 26	 if (item) {
 27	 damage.source = ActionFlow._damageSource(actor, item);
 28	 }
 29	
 30	 this._applyModifiableValue(damage, actor);
 31	 damage.value = Helpers.calcTotal(damage, { min: 0 });
 32	
 33	 this._applyModifiableValue(damage.ap, actor);
 34	 damage.ap.value = Helpers.calcTotal(damage.ap, { min: 0 });
 35	
 36	 return damage;
 37	 }
 38	
 39	 static _applyModifiableValue(value: Shadowrun.ModifiableValueLinked, actor: SR6Actor) {
 40	 const attribute = actor.findAttribute(value.attribute);
 41	 if (!attribute) return;
 42	
 43	 if (!value.base_formula_operator) {
 44	 console.error(`Unsupported formula operator: '${value.base_formula_operator}' used. Falling back to 'add'.`);
 45	 value.base_formula_operator = 'add';
 46	 }
 47	
 48	 // Avoid altering base OR value fields and raising the resulting damage on multiple function calls.
 49	 switch (value.base_formula_operator) {
 50	 case "add":
 51	 PartsList.AddUniquePart(value.mod, attribute.label, attribute.value);
 52	 break;
 53	 case "subtract":
 54	 PartsList.AddUniquePart(value.mod, attribute.label, -attribute.value);
 55	 break;
 56	 case "multiply":
 57	 PartsList.AddUniquePart(value.mod, 'SR6.Value', (value.base * attribute.value) - value.base);
 58	 break;
 59	 case "divide":
 60	 // Remove base from value by modifying.
 61	 PartsList.AddUniquePart(value.mod, 'SR6.BaseValue', value.base * -1);
 62	 // Add division result as modifier on zero.
 63	 const denominator = attribute.value === 0 ? 1 : attribute.value;
 64	 PartsList.AddUniquePart(value.mod, 'SR6.Value', Math.floor(value.base / denominator));
 65	 break;
 66	 }
 67	 }
 68	
