 75	 static _damageSource(actor: SR6Actor, item: SR6Item): Shadowrun.DamageSource {
 76	 return {
 77	 actorId: actor.id || '',
 78	 itemId: item.id || '',
 79	 itemName: item.name || '',
 80	 itemType: item.type
 81	 }
