Path: src/module/canvas.ts
 1	import {FLAGS, SYSTEM_NAME} from './constants';
 2	
 3	interface DistanceOptions {
 4	 gridSpaces?: boolean
 5	}
 6	
 7	/**
 8	 * Pulled from the FoundryVTT DND5e system: grid.mjs#measureDistance
 9	 * 
 10	 * @param segments 
 11	 * @param options 
 12	 * @returns 
 13	 */
 14	const measureDistances = function (segments, options: DistanceOptions = {}) {
 15	 if (!options.gridSpaces) return BaseGrid.prototype.measureDistances.call(this, segments, options);
 16	
 17	 // Track the total number of diagonals
 18	 let nDiagonal = 0;
 19	 const rule = this.diagonalRule;
 20	 const d = canvas.dimensions as Canvas.Dimensions;
 21	
 22	 // Iterate over measured segments
 23	 return segments.map((s) => {
 24	 let r = s.ray;
 25	
 26	 // Determine the total distance traveled
 27	 let nx = Math.abs(Math.ceil(r.dx / d.size));
 28	 let ny = Math.abs(Math.ceil(r.dy / d.size));
 29	
 30	 // Determine the number of straight and diagonal moves
 31	 let nd = Math.min(nx, ny);
 32	 let ns = Math.abs(ny - nx);
 33	 nDiagonal += nd;
 34	
 35	 // Estimate diagonal like other battle grid systems do. (DnD5e)
 36	 if (rule === '1-2-1') {
 37	 let nd10 = Math.floor(nDiagonal / 2) - Math.floor((nDiagonal - nd) / 2);
 38	 let spaces = nd10 * 2 + (nd - nd10) + ns;
 39	 // @ts-expect-error TODO: foundry-vtt-types v10
 40	 return spaces * canvas.dimensions.distance;
 41	 }
 42	
 43	 // Treat diagonal as straight line
 44	 else if (rule === 'EUCL') {
 45	 // @ts-expect-error TODO: foundry-vtt-types v10
 46	 // return Math.round(Math.hypot(nx, ny) * canvas.scene.data.gridDistance);
 47	 return Math.round(Math.hypot(nx, ny) * canvas.scene?.grid.distance);
 48	 }
 49	
 50	 // Treat diagonal as straight movement
 51	 // @ts-expect-error TODO: foundry-vtt-types v10
 52	 else return (ns + nd) * canvas.scene?.grid.distance;
 53	 });
 54	};
 55	
