 58	 //@ts-expect-error TODO: foundry-vtt-types v10
 59	 // Copy DnD5e's approach to movement measurement and add a custom field to the grid to be used in canvas.ts#measureDistances
 60	 canvas.grid.diagonalRule = game.settings.get(SYSTEM_NAME, FLAGS.DiagonalMovement);
 61	 // Add a custom measureDistances function, overwriting default to add more movement styles.
 62	 SquareGrid.prototype.measureDistances = measureDistances;
 63	}...
