 1	import {FLAGS, SYSTEM_NAME} from "../constants";
 2	import TrackType = Shadowrun.TrackType;
 3	
 4	export class SR6Token extends Token {
 5	 // @ts-expect-error Ignore getBarAttribute from Token
 6	 override _drawBar(number: number, bar: PIXI.Graphics, data: ReturnType<Token['getBarAttribute']>){
 7	 const tokenHealthBars = game.settings.get(SYSTEM_NAME, FLAGS.TokenHealthBars);
 8	 // FoundryVTT draws resource bars as full/good when the value is the
 9	 // same as the max and empty/bad at 0 (colored along a gradient).
 10	 // Shadowrun condition trackers count up from 0 to the maximum.
 11	 // We flip the values from Shadowrun format to FoundryVTT format here
 12	 // for drawing.
 13	 if (tokenHealthBars && data && data.attribute.startsWith('track')) {
 14	 const track = data as unknown as TrackType;
 15	 track.value = track.max - track.value;
 16	 }
 17	 super._drawBar(number, bar, data);
 18	 }
 19	}
