 247	
 248	 static orderKeys(obj) {
 249	 const keys = Object.keys(obj).sort(function keyOrder(k1, k2) {
 250	 if (k1 < k2) return -1;
 251	 if (k1 > k2) return +1;
 252	 return 0;
 253	 });
 254	
 255	 let i;
 256	 const after = {};
 257	 for (i = 0; i < keys.length; i++) {
 258	 after[keys[i]] = obj[keys[i]];
 259	 delete obj[keys[i]];
 260	 }
 261	
 262	 for (i = 0; i < keys.length; i++) {
 263	 obj[keys[i]] = after[keys[i]];
 264	 }
 265	 return obj;
 266	 }
 267	
 268	 static hasModifiers(event) {
 269	 return event && (event.shiftKey || event.altKey || event.ctrlKey || event.metaKey);
 270	 }
 271	
 272	 static filter(obj, comp) {
 273	 const retObj = {};
 274	 if (typeof obj === 'object' && obj !== null) {
 275	 Object.entries(obj).forEach(([key, value]) => {
 276	 if (comp([key, value])) retObj[key] = value;
 277	 });
 278	 }
 279	 return retObj;
 280	 }
