 152	 /**
 153	 * Highlight valid targets for an action
 154	 * @param action The action being performed
 155	 * @param actor The actor performing the action
 156	 */
 157	 static highlightValidTargets(action: SR6Item, actor: SR6Actor): void {
 158	 // Get all tokens in the scene
 159	 const tokens = canvas.tokens?.placeables || [];
 160	 
 161	 // Reset all token highlights
 162	 tokens.forEach(token => {
 163	 token.border?.visible = false;
 164	 });
 165	 
 166	 // Get valid targets
 167	 const validTargets = tokens.filter(token => 
 168	 token.actor && 
 169	 this.isValidTarget(token, action, actor)
 170	 );
 171	 
 172	 // Highlight valid targets
 173	 validTargets.forEach(token => {
 174	 token.border.visible = true;
 175	 token.border.style.borderColor = 0x00FF00; // Green for valid targets
 176	 });
 177	 
 178	 // Force a canvas refresh
 179	 canvas.tokens?.refresh();
 180	 }
