 465	 async useEdge(by: number = -1) {
 466	 const edge = this.getEdge();
 467	 if (edge && edge.value === 0) return;
 468	 // NOTE: There used to be a bug which could lower edge usage below zero. Let's quietly ignore and reset. :)
