 1563	 /**
 1564	 * Allow subclasses to override behavior after a test has finished.
 1565	 *
 1566	 * This can be used to alter values after a test is over.
 1567	 */
 1568	 async processResults() {
 1569	 const edgeQualities = new Map([
 1570	 ['Attribute Mastery: Agility', 'agility'],
 1571	 ['Attribute Mastery: Body', 'body'],
 1572	 ['Attribute Mastery: Charisma', 'charisma'],
 1573	 ['Attribute Mastery: Intuition', 'intuition'],
 1574	 ['Attribute Mastery: Reaction', 'reaction'],
 1575	 ['Attribute Mastery: Strength', 'strength'],
 1576	 ['Attribute Mastery: Willpower', 'willpower'],
 1577	 ['Analytical Mind', 'logic']
 1578	 ]);
 1579	 //['Attribute Mastery','Analytical Mind'];
 1580	 const processQuality = async (quality) =>{
 1581	
 1582	 if(edgeQualities.has(quality.name)) {
 1583	 const stat = edgeQualities.get(quality.name);
 1584	 if(this.data.action.attribute !== stat && this.data.action.attribute2 !== stat) {
 1585	 console.log(`Shadowrun 6e | ${quality.name} does not apply to this test`);
 1586	 return;
 1587	 }
 1588	 if(this.actor.system.combatRoundTracker.edgeGained >= 2) {
 1589	 this.data.edgeGain = {gained: true, discarded: true, effect: "No more edge may be gained this round."}
 1590	 return;
 1591	 } else if (this.actor?.system.attributes.edge.uses >= 7) {
 1592	 this.data.edgeGain = {gained: true, discarded: true, effect: "Edge pool is full"}
 1593	 return;
 1594	 }
 1595	 this.data.edgeGain = {gained: true, discarded: false, effect: quality.name}
 1596	 await this.actor.update({
 1597	 'system.combatRoundTracker.edgeGained': this.actor?.system.combatRoundTracker.edgeGained + 1,
 1598	 'system.attributes.edge.uses': this.actor?.system.attributes.edge.uses + 1
 1599	 })
 1600	 }
 1601	 }
 1602	
 1603	 this.actor?.items.filter(item => item.type === 'quality').forEach(quality => processQuality(quality));
 1604	
 1605	
 1606	 if (this.success) {
 1607	 await this.processSuccess();
 1608	 } else {
 1609	 await this.processFailure();
 1610	 }
 1611	 }
