// Add Matrix Actions to Selected Actors
// This macro adds all matrix actions from the matrix-actions compendium to selected actors

(async () => {
  // Check if any actors are selected
  if (!canvas.tokens.controlled.length && !game.user.character) {
    ui.notifications.warn("Please select at least one token or assign a character to your user");
    return;
  }

  // Get the matrix actions compendium
  const matrixPack = game.packs.get("sr6elysium.matrix-actions");
  if (!matrixPack) {
    ui.notifications.error("Matrix Actions compendium not found");
    return;
  }

  // Get all matrix actions
  await matrixPack.getIndex();
  const matrixActions = await Promise.all(
    Array.from(matrixPack.index).map(i => matrixPack.getDocument(i._id))
  );

  // Get the actors to update
  const actors = canvas.tokens.controlled.map(t => t.actor);
  if (game.user.character && !actors.length) {
    actors.push(game.user.character);
  }

  // Add matrix actions to each actor
  for (const actor of actors) {
    if (!actor) continue;

    // Get existing action names to avoid duplicates
    const existingNames = actor.items
      .filter(i => i.type === "action")
      .map(i => i.name.toLowerCase());

    // Filter out actions the actor already has
    const actionsToAdd = matrixActions.filter(
      a => !existingNames.includes(a.name.toLowerCase())
    );

    if (actionsToAdd.length === 0) {
      ui.notifications.info(`${actor.name} already has all matrix actions`);
      continue;
    }

    // Add the actions
    await actor.createEmbeddedDocuments(
      "Item",
      actionsToAdd.map(a => a.toObject())
    );

    ui.notifications.info(`Added ${actionsToAdd.length} matrix actions to ${actor.name}`);
  }
})();
